import React from "react";
import "./Mainpage.css";
import { BrowserRouter, Route, Link, Switch } from "react-router-dom";
import { useNavigate } from 'react-router-dom';
import "./Menu.css";
import Menu from "./Menu.js";
import bannerImage from './mvp-banner.png';
import Gallery from "./Gallery";

export default function Mainpage() {
  function Banner() {
    return (
      <div className='banner'>
          <img src={bannerImage} alt='banner' />
          <div className='banner-content'>
              <h2>KOD ACIKTIRIR</h2>
              <p>PIZZA, DOYURUR</p>
              <button className='siparis-butonu'>ACIKTIM</button>
          </div>
          <div className='banner-baslik'>
              <h3>Teknolojik Yemekler</h3>
          </div>
      </div>
    );
  }

  return (
    <div className="mainpage">
      <Banner />
      <Menu />
      <Gallery />
      <div className='yeni-alan'>
        <div className='yeni-alan-baslik'>
          <h3>Yeni Alan Başlık</h3>
        </div>
        <div className='yeni-alan-menu'>
          <Menu />
        </div>
        <div className='yeni-alan-gorseller'>
          <img src='gorsel1.jpg' alt='Gorsel 1' />
          <img src='gorsel2.jpg' alt='Gorsel 2' />
          <img src='gorsel3.jpg' alt='Gorsel 3' />
        </div>
      </div>
    </div>
  );
}
